import { Form, Input, Button, Space, Image } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import FisiomaisLogo from 'assets/images/logo_stroke_white.svg';
import { useDispatch, useSelector } from 'react-redux';
import { login } from 'store/currentUser';
import axios from 'axios';
import { useEffect } from 'react';
import { jwtDecode } from "jwt-decode";
import React, { useState } from 'react';

const LoginForm = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const currentUser = useSelector(state => state.currentUser.value);
    const [loginState, setLoginState] = useState("idle");

    const isLoading = loginState === "loading";
    const isSuccess = loginState === "success";
    const isError = loginState === "error";

    useEffect(() => {
        if (currentUser.user !== null) {
            return navigate('/')
        }
    }, [])

    const tryLogin = async (userCredentials) => {
        setLoginState("loading");
    
        const apiRoute = `${import.meta.env.VITE_API_BASE_ROUTE_SPRING}/auth`;
        let token = null;
    
        try {
            const response = await axios.post(apiRoute, userCredentials);
            token = response.data;
        } catch (error) {
            console.error('Error trying to login:', error.message);
            setLoginState("error");
        }
    
        return token;
    };

    const fetchUserData = async (token) => {
        const decoded = jwtDecode(token);
        console.log(decoded);
    
        const apiRoute = `${import.meta.env.VITE_API_BASE_ROUTE_SPRING}/auth/credentials/${decoded.id}`;
        let userData = null;
    
        try {
            const response = await axios.get(apiRoute, {
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Access-Control-Allow-Origin': '*',
                },
            });
            userData = response.data;
        } catch (error) {
            console.error('Error trying to fetch user data:', error.message);
        }
    
        return userData;
    };

    const onFinish = async values => {
        // console.log('Received values of form: ', values);
        const token = await tryLogin({
            email: values.username,
            senha: values.password,
        });
        if (token !== null) {
            console.info('Usu√°rio logado com sucesso. Token: ', token);
            const userData = await fetchUserData(token);
            console.log(userData);
            if (userData !== null && userData !== undefined && token !== null && token !== undefined) {
                dispatch(login({
                    user: userData,
                    token: token,
                }));
                setLoginState("success");
            } else {
                setLoginState("error");
            }
        }
    };

    if (currentUser.user !== null) {
        return null
    }

    return (
        <div>
            <Space style={{
                marginBottom: '50px',
            }}>
                <LogoImage
                    preview={false}
                    width={250}
                    height={250}
                    src={FisiomaisLogo}
                    fallback="data:image/png;base64,"
                />
            </Space>

            <Form
                name="normal_login"
                className="login-form"
                initialValues={{
                    remember: true,
                }}
                onFinish={onFinish}
            >
                <Form.Item
                    name="username"
                    rules={[
                        {
                            required: true,
                            message: 'Por favor, insira seu email!',
                        },
                    ]}
                >
                    <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Email" />
                </Form.Item>
                <Form.Item
                    name="password"
                    rules={[
                        {
                            required: true,
                            message: 'Por favor, insira sua senha!',
                        },
                    ]}
                >
                    <Input
                        prefix={<LockOutlined className="site-form-item-icon" />}
                        type="password"
                        placeholder="Senha"
                    />
                </Form.Item>

                <CustomFormItem>
                    <CustomLoginButton type="primary" htmlType="submit" className="login-form-button" loading={isLoading}>
                        Entrar
                    </CustomLoginButton>
                    <p style={{
                        textAlign: 'center',
                    }}> Ou </p>
                    <CustomLink href="">Cadastre Agora</CustomLink>
                </CustomFormItem>
            </Form>
        </div>
    )
}

export default LoginForm;

const CustomFormItem = styled(Form.Item)`
    margin-bottom: 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

const CustomLoginButton = styled(Button)`
    width: 100%;
    background-color: #00C3A5;
    border-color: #00C3A5;
    &:hover {
        background-color: #4ce2cc !important;
        border-color: white !important;
        color: #00C3A5 !important;
    }
`;

const CustomLink = styled(Link)`
    color: #00C3A5;
    font-weight: bold;
    text-decoration: none;
    &:hover {
        color: #00C3A5;
        text-decoration: underline;
    }
`;

const LogoImage = styled(Image)`
    width: 100%;
    height: 100%;
    margin-bottom: 50px;
`;